import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  const [quotes, setQuotes] = useState([]);
  const [randomQuotes, setRandomQuote] = useState("");
  const [randomAuthor, setRandomAuthor] = useState("");

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  useEffect(() => {
    fetchQuote();
    console.log(quotes);
    getRandomQuote();
  }, [setQuotes]);

  const fetchQuote = () => {
    fetch("https://goquotes-api.herokuapp.com/api/v1/random?count=1000")
      .then((response) => response.json())
      .then((data) => setQuotes(data.quotes));
  };

  const getRandomQuote = () => {
    setRandomQuote(quotes[Math.floor(Math.random() * quotes.length)].text);
    setRandomAuthor(quotes[Math.floor(Math.random() * quotes.length)].author);
  };

  return (
    <div className="App">
      <div id="quote-box">
        <h1 id="text"></h1>
        <span id="author"></span>
        <button id="new-quote" onClick={() => getRandomQuote()}>
          New Quote
        </button>
        <a id="tweet-quote" href="twitter.com/intent/tweet" target="_blank">
          hi
        </a>
      </div>
    </div>
  );
}

export default App;
