import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  const [quotes, setQuotes] = useState([]);
  const [randomQuote, setRandomQuote] = useState("");
  const [randomAuthor, setRandomAuthor] = useState("");

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  useEffect(() => {
    fetchQuotes();
    console.log(quotes);
  }, [setQuotes]);

  useEffect(() => {
    getRandomQuote();
  }, [quotes]);

  const fetchQuotes = async () => {
    await fetch("https://goquotes-api.herokuapp.com/api/v1/random?count=100")
      .then((response) => response.json())
      .then((data) => setQuotes(data.quotes));
  };

  const getRandomQuote = () => {
    let randomIndex = Math.floor(Math.random() * quotes.length);
    setRandomQuote(quotes[randomIndex].text);
    setRandomAuthor(quotes[randomIndex].author);
  };

  return (
    <div className="App">
      <div id="quote-box">
        <div id="quote-text">
          <span id="text">{randomQuote}</span>
        </div>
        <span id="author">{randomAuthor}</span>
        <button id="new-quote" onClick={() => getRandomQuote()}>
          New Quote
        </button>
        <a id="tweet-quote" href="twitter.com/intent/tweet" target="_blank"></a>
      </div>
    </div>
  );
}

export default App;
