import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  const [quote, setQuote] = useState("");

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  useEffect(() => {
    fetchQuote();
  }, [quote]);

  const fetchQuote = () => {
    fetch("https://goquotes-api.herokuapp.com/api/v1/random?count=1")
      .then((response) => response.json())
      .then((data) => setQuote(data.quotes));
  };

  return (
    <div className="App">
      <div id="quote-box">
        <h1 id="text">quote</h1>
        <span id="author"></span>
        <button id="new-quote"></button>
        <a id="tweet-quote" href="twitter.com/intent/tweet" target="_blank">
          hi
        </a>
      </div>
    </div>
  );
}

export default App;
