import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  let [quote, setQuote] = useState({});

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  useEffect(() => {
    fetchQuote();
    console.log(quote);
  }, [setQuote]);

  const fetchQuote = () => {
    fetch("https://goquotes-api.herokuapp.com/api/v1/random?count=1")
      .then((response) => response.json())
      .then(
        (data) =>
          (quote = {
            text: data.quotes[0].text,
            author: data.quotes[0].author,
          })
      );
    setQuote(quote);
  };

  return (
    <div className="App">
      <div id="quote-box">
        <h1 id="text"></h1>
        <span id="author"></span>
        <button id="new-quote">New Quote</button>
        <a id="tweet-quote" href="twitter.com/intent/tweet" target="_blank">
          hi
        </a>
      </div>
    </div>
  );
}

export default App;
