import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  const [quote, setQuote] = useState(null);
  const [randomQuote, setRandomQuote] = useState("");
  const [randomAuthor, setRandomAuthor] = useState("");

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(async () => {
    const response = await fetch(
      "https://goquotes-api.herokuapp.com/api/v1/random?count=1"
    );
    const data = await response.json();
    const [item] = data.quotes;
    setQuote(item);
  }, []);

  /*   const getRandomQuote = () => {
    let randomIndex = Math.floor(Math.random() * quotes.length);
    setRandomQuote(quotes[randomIndex].text);
    setRandomAuthor(quotes[randomIndex].author);
  }; */

  return (
    <div className="App">
      <div id="quote-box">
        <div id="quote-text">
          {quote && <span id="text">{quote.text}</span>}
        </div>
        <div id="quote-author">
          {quote && <span id="author">- {quote.author}</span>}
        </div>
        <div id="buttons">
          <button id="new-quote">New Quote</button>
          <a
            id="tweet-quote"
            href="twitter.com/intent/tweet"
            target="_blank"
          ></a>
        </div>
      </div>
    </div>
  );
}

export default App;
