import "./App.css";
import { useState, useEffect } from "react";

function App() {
  //State
  const [quote, setQuote] = useState(null);

  const colors = [
    "#16a085",
    "#27ae60",
    "#2c3e50",
    "#f39c12",
    "#e74c3c",
    "#9b59b6",
    "#FB6964",
    "#342224",
    "#472E32",
    "#BDBB99",
    "#77B1A9",
    "#73A857",
  ];

  // eslint-disable-next-line react-hooks/exhaustive-deps
  useEffect(async () => {
    const response = await fetch(
      "https://goquotes-api.herokuapp.com/api/v1/random?count=1"
    );
    const data = await response.json();
    const [item] = data.quotes;
    setQuote(item);
  }, []);

  return (
    <div className="App flex justify-center items-center min-h-screen bg-purple-400">
      <div
        id="quote-box"
        className="w-1/2 bg-white py-8 px-8 rounded-lg h-1/3 shadow-lg"
      >
        <div id="quote-text" className="mb-10">
          <i class="fas fa-quote-left fa-3x" className=""></i>
          {quote && (
            <span id="text" className="text-xl px-3 tracking-wide">
              {quote.text}
            </span>
          )}
        </div>
        <div id="quote-author" className="bg-purple-50">
          {quote && (
            <span id="author" className="italic">
              - {quote.author}
            </span>
          )}
        </div>
        <div
          id="buttons"
          className="py-3 flex justify-between flex-row-reverse"
        >
          <button id="new-quote">New Quote</button>
          <a id="tweet-quote" href="twitter.com/intent/tweet" target="_blank">
            <i class="fa fa-twitter"></i>
          </a>
        </div>
      </div>
    </div>
  );
}

export default App;
